import React, {useContext} from 'react';
import {GlobalContext,} from '../context/GlobalState';

const IncomeTrasactions = ({incomeTransaction}) => {
    const {deleteTransaction} = useContext(GlobalContext)

    return (
        <li className = "transaction">

            <span className="transaction-text">
                {incomeTransaction.incomeText}
            </span>

            <span className = "transaction-amount tooltip-on-hover" >
                ${incomeTransaction.incomeAmount}
            </span>

            <button className = "delete-btn"
            onClick={() => deleteTransaction(incomeTransaction.id)}>
                <i className="fas fa-times"></i>
            </button>
            <div className="tooltip">This is the tooltip</div>
        </li>
    );
};

export default IncomeTrasactions;
